import connection from "../configs/connectDB";
import bcrypt from "bcryptjs";
import Donor from "../models/donorModels";
import nodemailer from "nodemailer";
const { google } = require("googleapis");
const OAuth2 = google.auth.OAuth2;
import jwt from "jsonwebtoken";

let getHomepage = (req, res) => {
  return res.render("home_main.ejs");
};

let Nutripage = (req, res) => {
  return res.render("nutri.ejs");
};

let ShowLogin = (req, res) => {
  // return res.send("Login");
  return res.render("login.ejs", { layout: "./layouts/authentication" });
};

let Signup = (req, res) => {
  return res.render("Signup.ejs", { layout: "./layouts/authentication" });
};

let getForgot = (req, res) => {
  return res.render("pass_forgot.ejs", { layout: "./layouts/authentication" });
};

let getReset = (req, res) => {
  return res.render("pass_reset.ejs", { layout: "./layouts/authentication" });
};

let Register = (req, res) => {
  const {
    name,
    ssn,
    gender,
    birthday,
    phone,
    email,
    address,
    password,
    pass_confirm,
  } = req.body;

  let errors = [];

  // Check required fields
  if (
    !name ||
    !ssn ||
    !gender ||
    !birthday ||
    !phone ||
    !email ||
    !address ||
    !password ||
    !pass_confirm
  ) {
    errors.push({ msg: "Please fill in all fields" });
  }

  if (password !== pass_confirm) {
    errors.push({ msg: "Passwords do not match" });
  }

  if (password.length < 6) {
    errors.push({ msg: "Password should be at least 6 characters" });
  }

  if (errors.length > 0) {
    res.render("signup.ejs", {
      layout: "./layouts/authentication.ejs",
      errors,
      name,
      ssn,
      gender,
      birthday,
      phone,
      email,
      address,
      password,
      pass_confirm,
    });
  } else {
    var sql = "SELECT * FROM Donor WHERE email = ?";
    connection.query(sql, [email], (err, data) => {
      if (err) throw err;
      if (data.length > 0) {
        // User already exists
        errors.push({ msg: "Email ID already registered" });
        res.render("signup.ejs", {
          layout: "./layouts/authentication.ejs",
          errors,
          name,
          ssn,
          gender,
          birthday,
          phone,
          email,
          address,
          password,
          pass_confirm,
        });
      } else {
        const oauth2Client = new OAuth2(
          process.env.CLIENT_ID, // ClientID
          process.env.CLIENT_SECRET, // Client Secret
          process.env.REDIRECT_URI // Redirect URL
        );

        oauth2Client.setCredentials({
          refresh_token: process.env.REFRESH_TOKEN,
        });
        const accessToken = oauth2Client.getAccessToken();

        const token = jwt.sign(
          { name, ssn, gender, birthday, phone, email, address, password },
          process.env.ACCESS_TOKEN_SECRET,
          { expiresIn: "30m" }
        );

        const CLIENT_URL = "http://" + req.headers.host;

        const output = `
        <h2>Please click on below link to activate your account</h2>
        <p>${CLIENT_URL}/activate/${token}</p>
        <p><b>NOTE: </b> The above activation link expires in 30 minutes.</p>
        `;

        const transporter = nodemailer.createTransport({
          service: process.env.SERVICE,
          auth: {
            type: "OAuth2",
            user: "aidoctor.se@gmail.com",
            clientId: process.env.CLIENT_ID,
            clientSecret: process.env.CLIENT_SECRET,
            refreshToken: process.env.REFRESH_TOKEN,
            accessToken: accessToken,
          },
        });

        // send mail with defined transport object
        const mailOptions = {
          from: '"Auth Admin" <aidoctor.se@gmail.com>', // sender address
          to: email, // list of receivers
          subject: "Account Verification: NodeJS Auth ", // Subject line
          generateTextFromHTML: true,
          html: output, // html body
        };

        transporter.sendMail(mailOptions, (error, info) => {
          if (error) {
            console.log(error);
            req.flash(
              "error_msg",
              "Something went wrong on our end. Please register again."
            );
            res.redirect("/login");
          } else {
            console.log("Mail sent : %s", info.response);
            req.flash(
              "success_msg",
